import { useState } from "react";

export default function App() {
  return <Counter />;
}

function Counter() {
  const [step, setStep] = useState(1);

  return (
    <div>
      <button>Decrease</button>
      <p>Steps: {step}</p>
      <button>Increase</button>
    </div>
  );
}
